    private void SalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SalvarActionPerformed
        // TODO add your handling code here:
        try{
            
            PreparedStatement pst = conec.conn.prepareStatement("insert into estados(nome,nCat,descricao)values(?,?,?)"); //passagem do sql para inserção")
                pst.setString(1,nome.getText());
                pst.setString(2,nCat.getText());
                pst.setString(3,descricao.getText());
                pst.execute();
            JOptionPane.showMessageDialog(rootPane, "Alterado com sucesso!");
        } catch (SQLException ex) {
            JOptionPane.showMessageDialog(rootPane, "Erro na inserção \n Erro:" + ex);
        }

    }//GEN-LAST:event_SalvarActionPerformed

    private void existenteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_existenteActionPerformed
        if(existente.isSelected()){
            nova.setSelected(false);
        }
    }//GEN-LAST:event_existenteActionPerformed

    private void novaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_novaActionPerformed
        if(nova.isSelected()){
            existente.setSelected(false);
        }
    }//GEN-LAST:event_novaActionPerformed

